package model;

public class Ban669 {
	private int maBan;
	private String ten;
	private int toiDa;
	private String moTa;
	public Ban669() {
		// TODO Auto-generated constructor stub
	}
	public Ban669(int maBan, String ten, int toiDa, String moTa) {
		super();
		this.maBan = maBan;
		this.ten = ten;
		this.toiDa = toiDa;
		this.moTa = moTa;
	}
	public int getMaBan() {
		return maBan;
	}
	public void setMaBan(int maBan) {
		this.maBan = maBan;
	}
	public String getTen() {
		return ten;
	}
	public void setTen(String ten) {
		this.ten = ten;
	}
	public int getToiDa() {
		return toiDa;
	}
	public void setToiDa(int toiDa) {
		this.toiDa = toiDa;
	}
	public String getMoTa() {
		return moTa;
	}
	public void setMoTa(String moTa) {
		this.moTa = moTa;
	}
	
}
