package model;

public class MonAn669 {
	private int ma;
	private String ten;
	private String loai;
	private double gia;
	private String moTa;
	private String hinhAnh;
	
	public MonAn669() {
		// TODO Auto-generated constructor stub
	}

	public MonAn669(int ma, String ten, String loai, double gia, String moTa, String hinhAnh) {
		super();
		this.ma = ma;
		this.ten = ten;
		this.loai = loai;
		this.gia = gia;
		this.moTa = moTa;
		this.hinhAnh = hinhAnh;
	}

	public int getMa() {
		return ma;
	}

	public void setMa(int ma) {
		this.ma = ma;
	}

	public String getTen() {
		return ten;
	}

	public void setTen(String ten) {
		this.ten = ten;
	}

	public String getLoai() {
		return loai;
	}

	public void setLoai(String loai) {
		this.loai = loai;
	}

	public double getGia() {
		return gia;
	}

	public void setGia(double gia) {
		this.gia = gia;
	}

	public String getMoTa() {
		return moTa;
	}

	public void setMoTa(String moTa) {
		this.moTa = moTa;
	}

	public String getHinhAnh() {
		return hinhAnh;
	}

	public void setHinhAnh(String hinhAnh) {
		this.hinhAnh = hinhAnh;
	}
	
}
