package model;



public class NguyenLieu669 {
	private int ma;
	private String ten;
	private double gia;
	private String loai;
	
	public NguyenLieu669() {
		// TODO Auto-generated constructor stub
	}

	public NguyenLieu669(int ma, String ten, double gia, String loai) {
		super();
		this.ma = ma;
		this.ten = ten;
		this.gia = gia;
		this.loai = loai;
	}

	public int getMa() {
		return ma;
	}

	public void setMa(int ma) {
		this.ma = ma;
	}

	public String getTen() {
		return ten;
	}

	public void setTen(String ten) {
		this.ten = ten;
	}

	public double getGia() {
		return gia;
	}

	public void setGia(double gia) {
		this.gia = gia;
	}

	public String getLoai() {
		return loai;
	}

	public void setLoai(String loai) {
		this.loai = loai;
	}
	
	
}
